public class logicalbitwise {

	/**
	 * @param args
	 */
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
       int a=10,b=20,c=30;
       System.out.println(a>b && b>a);
       System.out.println(a);
       System.out.println(a>c & a++>c);
      System.out.println(a);
	}

}
